import Objeto from '@/services/objetos';
export default {
  objetos: [],
  SelectedObjeto: null,

  data() {
    return {
      objetos: [],
      SelectedObjeto: null
    };
  },

  mounted() {
    Objeto.getAll().then(resposta => {
      console.log(resposta.data);
      this.objetos = resposta.data;
    });
  },

  methods: {
    cadastroObjeto(SelectedObjeto) {
      if (SelectedObjeto != null) this.$router.push({
        name: 'objetoCadastro',
        params: {
          id: SelectedObjeto
        }
      });
    }

  }
};
